import React, { useEffect, useState } from "react";
import "./main.css";
import "./processing.css";

function Processing() {
  const [keyword, setKeyword] = useState(""); // keyword saved from Keyword page
  const [progress, setProgress] = useState(25); // percent done (backend will later set this)
  const [status, setStatus] = useState({
    objectDetection: "completed",
    musicRecommendation: "processing",
    facialExpression: "pending",
    storyGeneration: "pending",
  });

  // Later this will call your backend API (e.g., /api/progress)
  // and update status + progress accordingly
  useEffect(() => {
     const storedKeyword = localStorage.getItem("selectedKeyword");
    if (storedKeyword) {
      setKeyword(storedKeyword);
    }
    // Example: simulate API polling
    const interval = setInterval(() => {
      // Here you’ll call your backend API to get updated progress
      // fetch("/api/status").then(res => res.json()).then(data => { setProgress(data.progress); setStatus(data.status); });
    }, 3000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div>
      <h1 className="top-heading">
        <img
          src={`${process.env.PUBLIC_URL}/fyp-images/iconmusic.webp`}
          width="50"
          height="55"
          alt="MoodMelody Logo"
        />{" "}
        MoodMelody
      </h1>

      <p className="top-Description">AI Processing in Progress</p>

      <div className="uploads-flex">
        <img
          src={`${process.env.PUBLIC_URL}/fyp-images/vedio.jpg`}
          alt="Video"
          className="vedio-icon"
        />

        {/* ✅ Show keyword inside quotation marks */}
        <h3 className="keyword-heading">
          Keyword = “{keyword || "—"}”
        </h3>
      </div>

      <div className="progress-bar">
        <div className="progress-fill"></div>
      </div>

      <div className="processing-steps">
        <div className="step">
          <img
            src={`${process.env.PUBLIC_URL}/fyp-images/check.png`}
            alt="Done"
            className="step-icon"
          />
          <p>Object Detection</p>
        </div>
        <div className="step">
          <img
            src={`${process.env.PUBLIC_URL}/fyp-images/loading.png`}
            alt="Processing"
            className="step-icon"
          />
          <p>Music Recommendation</p>
        </div>
        <div className="step">
          <img
            src={`${process.env.PUBLIC_URL}/fyp-images/pending.png`}
            alt="Pending"
            className="step-icon"
          />
          <p>Facial Expression Analysis</p>
        </div>
        <div className="step">
          <img
            src={`${process.env.PUBLIC_URL}/fyp-images/pending.png`}
            alt="Pending"
            className="step-icon"
          />
          <p>Story Generation</p>
        </div>
      </div>

      <div className="status-section">
        <p>Completed</p>
        <p>Processing...</p>
        <p>Pending</p>
        <p>Pending</p>
      </div>

      <button className="cancel-btn">Cancel Processing</button>

    </div>
  );
}

export default Processing;
