import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import './main.css';
import './result.css';

function Result() {
  const navigate = useNavigate();
  const [resultData, setResultData] = useState({
    detectedObjects: [],
    dominantEmotion: "",
    recommendedSong: { track_name: "", artist: "", url: "" },
    story: "",
    keyword: localStorage.getItem("selectedKeyword") || "Adventure",
  });
  const [loading, setLoading] = useState(true);

  // Fetch result data from backend
  const fetchResult = async () => {
    try {
      // 1Ô∏è‚É£ Get video processing data from backend
      const objects = JSON.parse(localStorage.getItem("detected_objects") || "[]");
      const dominantEmotion = localStorage.getItem("dominant_emotion") || "happy";
      const recommendedSong = JSON.parse(localStorage.getItem("recommended_song") || "{}");
      const keyword = localStorage.getItem("selectedKeyword") || "Adventure";

      // 2Ô∏è‚É£ Generate story using backend
      const storyRes = await fetch("http://127.0.0.1:5000/generate_story", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ objects, keyword }),
      });
      const storyData = await storyRes.json();

      setResultData({
        detectedObjects: objects.map(o => o.label),
        dominantEmotion,
        recommendedSong,
        story: storyData.story,
        keyword,
      });

      setLoading(false);
    } catch (err) {
      console.error("Error fetching result data:", err);
      alert("Failed to fetch result from backend.");
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchResult();
  }, []);

  const handleRegenerateStory = async () => {
    try {
      const objects = JSON.parse(localStorage.getItem("detected_objects") || "[]");
      const keyword = localStorage.getItem("selectedKeyword");

      const response = await fetch("http://127.0.0.1:5000/generate_story", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ objects, keyword }),
      });

      const data = await response.json();
      setResultData(prev => ({ ...prev, story: data.story }));
      alert("‚ú® New story generated!");
    } catch (error) {
      console.error("Error regenerating story:", error);
      alert("Failed to regenerate story.");
    }
  };

  if (loading) return <p className="loading-text">Loading your personalized result...</p>;

  return (
    <div>
      <h1 className="top-heading">
        <img
          src={`${process.env.PUBLIC_URL}/fyp-images/iconmusic.webp`}
          width="50"
          height="55"
          alt="MoodMelody Logo"
        />{" "}
        MoodMelody
      </h1>

      <p className="top-Description">Your Personalized Music and Story are Ready</p>

      {/* Detection Summary */}
      <div className="container" id="mini-cont">
        <div className="row text-center">
          <div className="col-4"><h3 className="detections">Detected Expression</h3></div>
          <div className="col-4"><h3 className="detections">Keyword</h3></div>
          <div className="col-4"><h3 className="detections">Confidence</h3></div>
        </div>

        <div className="row text-center align-items-center mt-2">
          <div className="col-4"><h5 className="detected">{resultData.dominantEmotion}</h5></div>
          <div className="col-4"><h5 className="detected">‚Äú{resultData.keyword}‚Äù</h5></div>
          <div className="col-4">
            <h5 className="detected" style={{color: "green"}}>100%</h5>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="container" id="main-cont">
        <div className="row">
          {/* Music */}
          <div className="col-md-4" id="music-sug">
            <h3 className="detections">
              <img
                src={`${process.env.PUBLIC_URL}/fyp-images/icon.png`}
                width="30"
                height="30"
                alt="Music Icon"
                className="logo-icon"
              /> Recommended Music
            </h3>

            <div className="music-details">
              <h4 className="music-name">
                <img
                  src={`${process.env.PUBLIC_URL}/fyp-images/iconmusic.webp`}
                  width="35"
                  height="35"
                  alt="Music Icon"
                  className="logo-icon"
                /> {resultData.recommendedSong.track_name}
              </h4>
              <p className="artist-name">{resultData.recommendedSong.artist}</p>
              <a href={resultData.recommendedSong.url} target="_blank" rel="noreferrer" className="music-link">
                Listen on Spotify üéµ
              </a>
            </div>
          </div>

          {/* Story */}
          <div className="col-md-7 offset-md-1" id="story">
            <h3 className="detections">
              <img
                src={`${process.env.PUBLIC_URL}/fyp-images/story.jpg`}
                width="30"
                height="30"
                alt="Story Icon"
                className="logo-icon2"
              /> Generated Story
            </h3>

            <div className="story-details">
              <h5 className="detected-objects">Detected Objects:</h5>
              <div className="object-list">
                {resultData.detectedObjects.map((obj, i) => (
                  <span key={i} className="object-badge">{obj}</span>
                ))}
              </div>

              <p className="story-text">{resultData.story}</p>
            </div>
          </div>
        </div>
      </div>

      {/* Actions */}
      <div className="action-buttons">
        <button className="tryanother-btn" onClick={() => navigate("/upload")}>
          üé• Try Another Video
        </button>
        <button className="regen-btn" onClick={handleRegenerateStory}>
          üîÑ Re-generate Story
        </button>
      </div>
    </div>
  );
}

export default Result;
