import React, { useState } from "react";
import "./uploads.css";
import "./main.css";
import { Link, useNavigate } from "react-router-dom";

function Upload() {
  const [selectedFile, setSelectedFile] = useState(null);
  const navigate = useNavigate();

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      alert("Please select a video file first!");
      return;
    }

    const formData = new FormData();
    formData.append("video", selectedFile);

    try {
      const response = await fetch("http://127.0.0.1:5000/upload", {
        method: "POST",
        body: formData,
      });

      if (!response.ok) {
        const text = await response.text(); // detailed backend error
        throw new Error(text || "Upload failed");
      }

      const data = await response.json();

      // Save backend response to localStorage
      localStorage.setItem("detected_objects", JSON.stringify(data.objects));
      localStorage.setItem("dominant_emotion", data.dominant_emotion);
      localStorage.setItem("recommended_song", JSON.stringify(data.recommended_song));

      alert("✅ Video processed successfully!");
      navigate("/keyword"); // move to keyword page
    } catch (error) {
      console.error("Error uploading video:", error);
      alert("Error uploading video: " + error.message);
    }
  };

  return (
    <div>
      <h1 className="top-heading">MoodMelody</h1>
      <h2 className="sub-Heading">Share Your Moment</h2>
      <h4 className="Description">
        Upload a video and let our AI create a personalized story and suggest music for you...
      </h4>

      <div className="upload-container">
        <input
          id="videoInput"
          type="file"
          accept="video/*"
          onChange={handleFileChange}
        />
        {selectedFile && (
          <div className="video-preview">
            <video width="300" height="250" controls>
              <source src={URL.createObjectURL(selectedFile)} type="video/mp4" />
            </video>
          </div>
        )}
        <button className="upload-btn" onClick={handleUpload}>
          Upload
        </button>
      </div>
    </div>
  );
}

export default Upload;
