import React, { useState } from "react";
import "./main.css";
import "./keywords.css";

function Keyword() {
  const [keyword, setKeyword] = useState("");

  const handleChange = (event) => {
    setKeyword(event.target.value);
  };

  const handleSelect = (word) => {
    setKeyword(word);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (keyword.trim() === "") {
      alert("‚ö†Ô∏è Please enter a keyword!");
      return;
    }
    alert(`üéµ Your keyword: "${keyword}"`);
  };

  return (
    <div>
      <h1 className="top-heading">
        <img
          src={`${process.env.PUBLIC_URL}/fyp-images/iconmusic.webp`}
          width="50"
          height="55"
          alt="MoodMelody Logo"
        />{" "}
        MoodMelody
      </h1>

      <blockquote className="styled-quote">
        <p>
          <img
            src={`${process.env.PUBLIC_URL}/fyp-images/movie.webp`}
            width="35"
            height="30"
            alt="Mood Icon"
            className="movie"
          />
          From Moments to Melodies ‚Äî Create Stories from Your Videos...
        </p>
      </blockquote>

      <h2 className="main-heading">Add Your Inspiration Keyword</h2>

      <form className="keyword-form" onSubmit={handleSubmit}>
        <input
          type="text"
          className="keyword-input"
          placeholder="Enter a word that captures your video (e.g., adventure, peace...)"
          value={keyword}
          onChange={handleChange}
        />

        {/* ‚úÖ Keyword suggestions BEFORE the Submit button */}
        <div className="keyword-suggestions">
          <div className="row">
            <div className="col" onClick={() => handleSelect("Adventure")}>Adventure</div>
            <div className="col" onClick={() => handleSelect("Peace")}>Peace</div>
            <div className="col" onClick={() => handleSelect("Happiness")}>Happiness</div>
            <div className="col" onClick={() => handleSelect("Romance")}>Romance</div>
          </div>
          <div className="row">
            <div className="col" onClick={() => handleSelect("Nature")}>Nature</div>
            <div className="col" onClick={() => handleSelect("Excitement")}>Excitement</div>
            <div className="col" onClick={() => handleSelect("Memories")}>Memories</div>
            <div className="col" onClick={() => handleSelect("Dreams")}>Dreams</div>
          </div>
        </div>

        <button type="submit" className="keyword-btn">
          Submit
        </button>
      </form>
    </div>
  );
}

export default Keyword;
