import './main.css';
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

function Processing() {
  const navigate = useNavigate();

  const [status, setStatus] = useState({
    objectDetection: "pending",
    musicRecommendation: "pending",
    emotionAnalysis: "pending",
    storyGeneration: "pending"
  });

  const [result, setResult] = useState({
    detected_objects: [],
    dominant_emotion: "",
    recommended_song: {},
    generated_story: ""
  });

  // Poll backend for status every 1 second
  useEffect(() => {
    const interval = setInterval(async () => {
      try {
        const res = await fetch("http://127.0.0.1:5000/status");
        const data = await res.json();

        if (data.status) setStatus(data.status);
        if (data.result) setResult(data.result);

        // If all completed, stop polling and save to localStorage
        if (
          Object.values(data.status).every(val => val === "completed")
        ) {
          clearInterval(interval);

          // Save results to localStorage
          localStorage.setItem("detected_objects", JSON.stringify(data.result.detected_objects || []));
          localStorage.setItem("dominant_emotion", data.result.dominant_emotion || "neutral");
          localStorage.setItem("recommended_song", JSON.stringify(data.result.recommended_song || {}));
          localStorage.setItem("generated_story", data.result.generated_story || "");

          // Navigate to Result page after a short delay
          setTimeout(() => navigate("/result"), 1000);
        }

      } catch (error) {
        console.error("Error fetching status:", error);
      }
    }, 1000);

    return () => clearInterval(interval);
  }, [navigate]);

  // Render status
  const renderStep = (label, value) => (
    <div className="status-row">
      <span>{label}:</span>
      <span
        style={{
          color: value === "completed" ? "green" :
                 value === "processing" ? "orange" : "gray"
        }}
      >
        {value}
      </span>
    </div>
  );

  return (
    <div className="processing-container">
      <h1 className="top-heading">
        <img
          src={`${process.env.PUBLIC_URL}/fyp-images/iconmusic.webp`}
          width="50"
          height="55"
          alt="MoodMelody Logo"
        />{" "}
        MoodMelody
      </h1>
      <p className="top-Description">AI Processing in Progress</p>

      <div className="processing-status">
        {renderStep("Object Detection", status.objectDetection)}
        {renderStep("Music Recommendation", status.musicRecommendation)}
        {renderStep("Facial Expression Analysis", status.emotionAnalysis)}
        {renderStep("Story Generation", status.storyGeneration)}
      </div>

      <div className="object-preview">
        <h5>Detected Objects:</h5>
        <div className="object-list">
          {result.detected_objects?.length
            ? result.detected_objects.map((obj, idx) => (
                <span key={idx} className="object-badge">{obj.label}</span>
              ))
            : <span className="object-badge">None</span>
          }
        </div>
      </div>

      <div className="action-buttons">
        <button
          className="cancel-btn"
          onClick={() => navigate("/upload")}
        >
          ❌ Cancel Processing
        </button>
      </div>
    </div>
  );
}

export default Processing;
