import React, { useEffect, useState } from "react";

export default function ResultPage() {
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchResult = async () => {
      try {
        const res = await fetch("http://127.0.0.1:5000/status");
        const data = await res.json();
        console.log("‚úÖ Result data:", data.result);
        setResult(data.result);
        setLoading(false);
      } catch (err) {
        console.error("‚ùå Error fetching result:", err);
      }
    };

    fetchResult();
  }, []);

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen bg-gray-50">
        <p className="text-lg font-medium text-gray-600">
          Fetching your results...
        </p>
      </div>
    );
  }

  if (!result) {
    return (
      <div className="flex items-center justify-center h-screen bg-gray-50">
        <p className="text-lg text-gray-600">No result data available üò¢</p>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 py-10 px-6">
      <div className="max-w-4xl mx-auto bg-white shadow-md rounded-2xl p-8">
        <h1 className="text-3xl font-bold text-center text-indigo-700 mb-6">
          üé¨ Analysis Results
        </h1>

        {/* Emotion */}
        <div className="mb-6">
          <h2 className="text-xl font-semibold text-gray-800 mb-2">
            Detected Emotion:
          </h2>
          <p className="text-lg text-indigo-600 capitalize">
            {result.dominant_emotion}
          </p>
        </div>

        {/* Detected Objects */}
        <div className="mb-6">
          <h2 className="text-xl font-semibold text-gray-800 mb-2">
            Detected Objects:
          </h2>
          {result.detected_objects.length > 0 ? (
            <ul className="list-disc list-inside space-y-1">
              {result.detected_objects.map((obj, index) => (
                <li key={index} className="text-gray-700">
                  {obj.label} ‚Äî{" "}
                  <span className="text-sm text-gray-500">
                    {Math.round(obj.avg_conf * 100)}% confidence
                  </span>
                </li>
              ))}
            </ul>
          ) : (
            <p className="text-gray-600">No objects detected.</p>
          )}
        </div>

        {/* Recommended Song */}
        <div className="mb-6">
          <h2 className="text-xl font-semibold text-gray-800 mb-2">
            üéµ Recommended Song:
          </h2>
          <p className="text-gray-700">
            <strong>{result.recommended_song.track_name}</strong> by{" "}
            {result.recommended_song.artist}
          </p>
          <a
            href={result.recommended_song.url}
            target="_blank"
            rel="noopener noreferrer"
            className="inline-block mt-2 text-indigo-600 hover:underline"
          >
            Listen on Spotify
          </a>
        </div>

        {/* Generated Story */}
        <div className="mb-6">
          <h2 className="text-xl font-semibold text-gray-800 mb-2">
            üìñ Generated Story:
          </h2>
          <p className="text-gray-700 leading-relaxed">
            {result.generated_story || "No story generated."}
          </p>
        </div>

        {/* Back Button */}
        <div className="text-center mt-8">
          <a
            href="/"
            className="inline-block bg-indigo-600 text-white px-6 py-3 rounded-lg font-medium hover:bg-indigo-700 transition"
          >
            Upload Another Video
          </a>
        </div>
      </div>
    </div>
  );
}
