import React, { useState } from "react";
import "./uploads.css";
import "./main.css";
import { Link } from "react-router-dom";

function Upload() {
  const handleUpload = async () => {
  if (!selectedFile) {
    alert("Please select a video file first!");
    return;
  }

  const formData = new FormData();
  formData.append("video", selectedFile);

  try {
    const response = await fetch("http://127.0.0.1:5000/upload", {
      method: "POST",
      body: formData,
    });

    if (!response.ok) throw new Error("Upload failed");

    const data = await response.json();

    // Save backend response to localStorage
    localStorage.setItem("detected_objects", JSON.stringify(data.objects));
    localStorage.setItem("dominant_emotion", data.dominant_emotion);
    localStorage.setItem("recommended_song", JSON.stringify(data.recommended_song));

    alert("✅ Video processed successfully!");
  } catch (error) {
    console.error("Error uploading video:", error);
    alert("Error uploading video. Please try again.");
  }
};

  return (
    <div>
      <h1 className="top-heading">
        <img
          src={`${process.env.PUBLIC_URL}/fyp-images/iconmusic.webp`}
          width="50"
          height="55"
          alt="MoodMelody Logo"
        />{" "}
        MoodMelody
      </h1>

      <h2 className="sub-Heading">Share Your Moment</h2>
      <h4 className="Description">
        Upload a video and let our AI create a personalized story and suggest
        music for you...
      </h4>

      <div className="upload-container">
        <h4 className="Description2">
          Drag your video here or click to browse
        </h4>

        <div className="upload-flex">
          <img
            src={`${process.env.PUBLIC_URL}/fyp-images/Video-512.webp`}
            alt="Upload Video Icon"
            className="upload-icon"
          />

          {/* Custom upload button */}
          <label htmlFor="videoInput" className="custom-file-upload">
            {selectedFile ? selectedFile.name : "Choose Video"}
          </label>
          <input
            id="videoInput"
            type="file"
            accept="video/*"
            onChange={handleFileChange}
          />
        </div>

        {selectedFile && (
          <div className="video-preview">
            <video width="350" controls>
              <source src={URL.createObjectURL(selectedFile)} type="video/mp4" />
            </video>
          </div>
        )}
        <div class="container" id="cont2">
          <div class="row" id="cont2r1">
            <div class="col-2">MP4</div>
            <div class="col-2">MOV</div>
            <div class="col-2">AVI</div>
            <div class="col-4">Max 100MB</div>
          </div>
        </div>
        <button className="upload-btn" onClick={handleUpload}>
          Upload
        </button>
      </div>
      <nav aria-label="Page navigation" className="page-nav">
        <ul className="pagination-nav">
          <li className="page-item">
            <Link to="/" className="page-link">
              <img
                src={`${process.env.PUBLIC_URL}/fyp-images/previous.png`}
                width="40"
                height="45"
                alt="Previous"
                className="previous"
              />
            </Link>
          </li>
          <li className="page-item next-link">
            <Link to="/keyword" className="page-link">
              <img
                src={`${process.env.PUBLIC_URL}/fyp-images/next.png`}
                width="40"
                height="45"
                alt="Next"
                className="next"
              />
            </Link>
          </li>
        </ul>
      </nav>
    </div>
  );
}

export default Upload;
