import React, { useEffect, useState } from "react";
import "./main.css";
import "./processing.css";
import { useNavigate } from "react-router-dom";

function Processing() {
  const [status, setStatus] = useState({
    objectDetection: "pending",
    musicRecommendation: "pending",
    emotionAnalysis: "pending",
    storyGeneration: "pending",
  });
  const navigate = useNavigate();

  const statusIcons = {
    completed: `${process.env.PUBLIC_URL}/fyp-images/tick.png`,
    processing: `${process.env.PUBLIC_URL}/fyp-images/inprogress.png`,
    pending: `${process.env.PUBLIC_URL}/fyp-images/pending.jpeg`,
  };

  const keyword = localStorage.getItem("selectedKeyword") || "";

  useEffect(() => {
    const interval = setInterval(async () => {
      try {
        const response = await fetch("http://127.0.0.1:5000/status");
        if (!response.ok) throw new Error("Backend error");

        const text = await response.text();
        if (!text) return; // skip empty responses

        let data;
        try {
          data = JSON.parse(text);
        } catch {
          console.error("Invalid JSON:", text);
          return;
        }

        setStatus(data.status || {});
        // Navigate when all steps are completed
        if (Object.values(data.status || {}).every(s => s === "completed")) {
          clearInterval(interval);
          navigate("/result", { state: data.result || {} });
        }
      } catch (err) {
        console.error(err);
      }
    }, 1000);

    return () => clearInterval(interval);
  }, [navigate]);

  const progress = (Object.values(status).filter(s => s === "completed").length / 4) * 100;

  return (
    <div className="keyword-page">
      <h1 className="top-heading">
        <img
          src={`${process.env.PUBLIC_URL}/fyp-images/iconmusic.webp`}
          width="50"
          height="55"
          alt="MoodMelody Logo"
        /> MoodMelody
      </h1>

      <p className="top-Description">AI Processing in Progress</p>

      <div className="uploads-flex">
        <img
          src={`${process.env.PUBLIC_URL}/fyp-images/vedio.jpg`}
          alt="Video"
          className="vedio-icon"
        />
        <h3 className="keyword-heading">
          Keyword = “{keyword || "—"}”
        </h3>
      </div>

      <div className="progress-bar">
        <div className="progress-fill" style={{ width: `${progress}%` }}></div>
      </div>

      <div className="processing-content">
        {Object.keys(status).map(step => (
          <div className="step" key={step}>
            <img src={statusIcons[status[step]]} alt="status" className="step-icon" />
            <p>{step}</p>
          </div>
        ))}

        <div className="status-section">
          <p>Object Detection: {status.objectDetection}</p>
          <p>Music Recommendation: {status.musicRecommendation}</p>
          <p>Facial Expression Analysis: {status.emotionAnalysis}</p>
          <p>Story Generation: {status.storyGeneration}</p>
        </div>
      </div>

      <button className="cancel-btn" onClick={() => navigate("/keyword")}>
        Cancel Processing
      </button>
    </div>
  );
}

export default Processing;
