import React, { useEffect, useState } from "react";
import "./main.css";
import "./processing.css";
import { useNavigate } from "react-router-dom";

function Processing() {
  const navigate = useNavigate();
  const keyword = localStorage.getItem("selectedKeyword") || "";

  const [status, setStatus] = useState({
    objectDetection: "pending",
    musicRecommendation: "pending",
    emotionAnalysis: "pending",
    storyGeneration: "pending",
  });

  const [progress, setProgress] = useState(0);

  const statusIcons = {
    completed: `${process.env.PUBLIC_URL}/fyp-images/tick.png`,
    processing: `${process.env.PUBLIC_URL}/fyp-images/inprogress.png`,
    pending: `${process.env.PUBLIC_URL}/fyp-images/pending.jpeg`,
  };

  useEffect(() => {
    const interval = setInterval(async () => {
      try {
        const res = await fetch("http://127.0.0.1:5000/status");
        if (!res.ok) throw new Error("Backend error");

        const data = await res.json();

        // Safe defaults
        const currentStatus = data?.status || status;
        setStatus(currentStatus);

        // Update progress dynamically
        const completedCount = Object.values(currentStatus).filter(
          (s) => String(s).toLowerCase() === "completed"
        ).length;
        setProgress((completedCount / 4) * 100);

        // Only navigate if everything completed
        const allCompleted = Object.values(currentStatus).every(
          (s) => String(s).toLowerCase() === "completed"
        );

        if (allCompleted) {
          clearInterval(interval);

          const result = data?.result || {};

          // Save backend results safely
          localStorage.setItem(
            "detected_objects",
            JSON.stringify(result.detected_objects || [])
          );
          localStorage.setItem(
            "dominant_emotion",
            result.dominant_emotion || "neutral"
          );
          localStorage.setItem(
            "recommended_song",
            JSON.stringify(result.recommended_song || {})
          );
          localStorage.setItem(
            "generated_story",
            result.generated_story || ""
          );

          navigate("/result");
        }
      } catch (err) {
        console.error("Processing error:", err);
      }
    }, 1000);

    return () => clearInterval(interval);
  }, [navigate, status]);

  return (
    <div className="keyword-page">
      <h1 className="top-heading">
        <img
          src={`${process.env.PUBLIC_URL}/fyp-images/iconmusic.webp`}
          width="50"
          height="55"
          alt="MoodMelody Logo"
        />{" "}
        MoodMelody
      </h1>

      <p className="top-Description">AI Processing in Progress</p>

      <div className="uploads-flex">
        <img
          src={`${process.env.PUBLIC_URL}/fyp-images/vedio.jpg`}
          alt="Video"
          className="vedio-icon"
        />
        <h3 className="keyword-heading">Keyword = “{keyword || "—"}”</h3>
      </div>

      <div className="progress-bar">
        <div className="progress-fill" style={{ width: `${progress}%` }}></div>
      </div>

      <div className="processing-content">
        {Object.keys(status).map((step) => (
          <div className="step" key={step}>
            <img
              src={statusIcons[status[step]]}
              alt="status"
              className="step-icon"
            />
            <p>{step}</p>
          </div>
        ))}
      </div>

      <button
        className="cancel-btn"
        onClick={() => navigate("/keyword")}
      >
        Cancel Processing
      </button>
    </div>
  );
}

export default Processing;
