// src/pages/Result.js
import { useEffect, useState } from "react";

export default function Result() {
  const [objects, setObjects] = useState([]);
  const [emotion, setEmotion] = useState("");
  const [song, setSong] = useState({});
  const [story, setStory] = useState("");
  const [videoUrl, setVideoUrl] = useState("");

  useEffect(() => {
    setObjects(JSON.parse(localStorage.getItem("detected_objects")) || []);
    setEmotion(localStorage.getItem("dominant_emotion") || "");
    setSong(JSON.parse(localStorage.getItem("recommended_song")) || {});
    setStory(localStorage.getItem("generated_story") || "");
    setVideoUrl(localStorage.getItem("uploaded_video_url") || "");
  }, []);

  return (
    <div className="result-container">
      <h2>‚ú® Processing Complete ‚ú®</h2>

      {videoUrl && (
        <video
          src={videoUrl}
          controls
          autoPlay
          loop
          width="480"
          height="270"
          style={{ borderRadius: "10px", boxShadow: "0 0 10px #aaa" }}
        />
      )}

      <div className="result-section">
        <h3>üßç Detected Objects:</h3>
        {objects.length > 0 ? (
          <ul>
            {objects.map((obj, i) => (
              <li key={i}>{obj}</li>
            ))}
          </ul>
        ) : (
          <p>No objects detected</p>
        )}
      </div>

      <div className="result-section">
        <h3>üß† Dominant Emotion:</h3>
        <p>{emotion}</p>
      </div>

      <div className="result-section">
        <h3>üéµ Recommended Song:</h3>
        <p>
          <strong>{song.track_name}</strong> by {song.artist}
        </p>
        {song.url && song.url !== "#" && (
          <a href={song.url} target="_blank" rel="noreferrer">
            Listen on Spotify
          </a>
        )}
      </div>

      <div className="result-section">
        <h3>üìñ Generated Story:</h3>
        <p>{story}</p>
      </div>
    </div>
  );
}
