// src/pages/Processing.js
import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

export default function Processing() {
  const navigate = useNavigate();
  const [status, setStatus] = useState({});
  const [videoUrl, setVideoUrl] = useState(null);

  useEffect(() => {
    const storedUrl = localStorage.getItem("uploaded_video_url");
    if (storedUrl) {
      setVideoUrl(storedUrl);
    }

    const interval = setInterval(async () => {
      try {
        const res = await fetch("http://127.0.0.1:5000/status");
        const data = await res.json();

        // ‚úÖ Flask now returns directly the status dictionary
        if (data.objectDetection && data.emotionAnalysis) {
          setStatus(data);
        } else if (data.status) {
          // in case Flask returns {status:{...}, result:{...}}
          setStatus(data.status);
        }

        // All steps completed ‚Üí move to Result page
        const allDone =
          Object.values(data.status || data).every(
            (v) => v === "completed"
          );

        if (allDone && data.result) {
          localStorage.setItem(
            "detected_objects",
            JSON.stringify(data.result.detected_objects || [])
          );
          localStorage.setItem(
            "dominant_emotion",
            data.result.dominant_emotion || "neutral"
          );
          localStorage.setItem(
            "recommended_song",
            JSON.stringify(data.result.recommended_song || {})
          );
          localStorage.setItem(
            "generated_story",
            data.result.generated_story || ""
          );
          navigate("/result");
        }
      } catch (err) {
        console.error("Error fetching status:", err);
      }
    }, 1000);

    return () => clearInterval(interval);
  }, [navigate]);

  return (
    <div className="processing-container">
      <h2>Processing Your Video...</h2>

      <div className="uploads-flex">
        {videoUrl ? (
          <video
            src={videoUrl}
            controls
            autoPlay
            muted
            loop
            width="480"
            height="270"
            style={{ borderRadius: "10px", boxShadow: "0 0 10px #aaa" }}
          />
        ) : (
          <p>No video found</p>
        )}
      </div>

      <div className="status-box">
        <p>üß† Emotion Analysis: {status.emotionAnalysis}</p>
        <p>üßç Object Detection: {status.objectDetection}</p>
        <p>üéµ Music Recommendation: {status.musicRecommendation}</p>
        <p>üìñ Story Generation: {status.storyGeneration}</p>
      </div>
    </div>
  );
}
