import React, { useState } from "react";
import "./main.css";
import "./keywords.css";

function Keyword() {
  const [keyword, setKeyword] = useState("");

  const handleChange = (event) => {
    setKeyword(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (keyword.trim() === "") {
      alert("‚ö†Ô∏è Please enter a keyword!");
      return;
    }
    alert(`üéµ Your keyword: "${keyword}"`);
    // Later: send keyword to backend here
  };

  return (
    <div>
      <h1 className="top-heading">
        <img
          src={`${process.env.PUBLIC_URL}/fyp-images/iconmusic.webp`}
          width="50"
          height="55"
          alt="MoodMelody Logo"
        />{" "}
        MoodMelody
      </h1>

      <blockquote className="styled-quote">
        <p>
          <img
            src={`${process.env.PUBLIC_URL}/fyp-images/movie.webp`}
            width="35"
            height="30"
            alt="MoodMelody Logo"
            className="movie"
          />
          From Moments to Melodies ‚Äî Create Stories from Your Videos...
        </p>
      </blockquote>

      <h2 className="keyword">Add Your Inspiration Keyword</h2>

      <form className="keyword-form" onSubmit={handleSubmit}>
        <input
          type="text"
          className="keyword-input"
          placeholder="Enter your keyword..."
          value={keyword}
          onChange={handleChange}
        />
        <button type="submit" className="keyword-btn">Submit</button>
      </form>
    </div>
  );
}

export default Keyword;
