import React, { useState } from "react";
import "./uploads.css";
import "./main.css";

function Upload() {
  const [selectedFile, setSelectedFile] = useState(null);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    setSelectedFile(file);
  };

  const handleUpload = () => {
    if (!selectedFile) {
      alert("Please select a video file first!");
      return;
    }
    // Later, connect to your backend API here
    alert(`ðŸŽ¬ Video "${selectedFile.name}" uploaded successfully!`);
  };

  return (
    <div>
      <h1 className="top-heading">
        <img
          src={`${process.env.PUBLIC_URL}/fyp-images/iconmusic.webp`}
          width="50"
          height="55"
          alt="MoodMelody Logo"
        />{" "}
        MoodMelody
      </h1>

      <h2 className="sub-Heading">Share Your Moment</h2>
      <h4 className="Description">
        Upload a video and let our AI create a personalized story and suggest
        music for you...
      </h4>

      <div className="upload-container">

        {/* Upload box */}
        <label htmlFor="videoInput" className="upload-box">
          <h4 className="Description2">
            {selectedFile
              ? `Selected: ${selectedFile.name}`
              : "ðŸŽž Drag your video here or click to browse"}
          </h4>
          <div className="upload-container">
            <div className="upload-flex">
              {/* Upload video icon */}
              <img
                src={`${process.env.PUBLIC_URL}/fyp-images/Video-512.webp`}
                alt="Upload Video Icon"
                className="upload-icon"
              />

              {/* File input */}
              <div className="file-section">
                <h4 className="Description2">Choose your video file</h4>
                <input
                  type="file"
                  id="videoInput"
                  accept="video/*"
                  onChange={handleFileChange}
                />
              </div>
            </div>
          </div>


        </label>

        {selectedFile && (
          <div className="video-preview">
            <video width="350" controls>
              <source src={URL.createObjectURL(selectedFile)} type="video/mp4" />
            </video>
          </div>
        )}

        <button className="upload-btn" onClick={handleUpload}>
          Upload
        </button>
      </div>
    </div>
  );
}

export default Upload;
