import './main.css';
import './result.css';
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

function Result() {
  const navigate = useNavigate();

  const [resultData, setResultData] = useState({
    emotion: "",
    keyword: "",
    confidence: 0,
    songName: "",
    artist: "",
    songDescription: "",
    detectedObjects: [],
    story: ""
  });

  useEffect(() => {
    // Fetch from localStorage safely
    const keyword = localStorage.getItem("selectedKeyword") || "Adventure";
    const emotion = localStorage.getItem("dominant_emotion") || "Happy";
    const confidence = parseInt(localStorage.getItem("confidence") || "87", 10);

    // Parse song data safely
    let song = { track_name: "Good as Hell", artist: "Lizzo" };
    try {
      const songData = localStorage.getItem("recommended_song");
      if (songData) song = JSON.parse(songData);
    } catch (err) {
      console.error("Invalid song JSON in localStorage", err);
    }

    // Parse detected objects safely
    let objectsList = [];
    try {
      const objData = localStorage.getItem("detected_objects");
      if (objData) {
        const parsed = JSON.parse(objData);
        objectsList = parsed.map(o => o.label || o);
      }
    } catch (err) {
      console.error("Invalid detected_objects JSON", err);
    }

    const story = localStorage.getItem("generated_story") || 
      "Standing before the majestic mountain with your trusty backpack, you embody the essence of adventure. Your smile radiates the excitement of discovery, the kind that only comes from exploring the unknown.";

    setResultData({
      emotion,
      keyword,
      confidence,
      songName: song.track_name || "Unknown",
      artist: song.artist || "Unknown",
      songDescription: `Perfect match for your ${emotion.toLowerCase()} ${keyword.toLowerCase()}!`,
      detectedObjects: objectsList,
      story
    });
  }, []);

  // Handler to regenerate story
  const handleRegenerateStory = async () => {
    try {
      const objects = JSON.parse(localStorage.getItem("detected_objects") || "[]");
      const keyword = localStorage.getItem("selectedKeyword") || "";

      const response = await fetch("http://127.0.0.1:5000/generate_story", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ objects, keyword }),
      });

      const data = await response.json();
      setResultData(prev => ({ ...prev, story: data.story || prev.story }));

      alert("✨ New story generated!");
    } catch (error) {
      console.error("Error regenerating story:", error);
    }
  };

  return (
    <div className="result-page">
      {/* Header */}
      <h1 className="top-heading">
        <img
          src={`${process.env.PUBLIC_URL}/fyp-images/iconmusic.webp`}
          width="50"
          height="55"
          alt="MoodMelody Logo"
        />{" "}
        MoodMelody
      </h1>

      <p className="top-Description">Your Personalized Music and Story are Ready</p>

      {/* Detection Overview */}
      <div className="container" id="mini-cont">
        <div className="row text-center">
          <div className="col-4"><h3 className="detections">Detected Expression</h3></div>
          <div className="col-4"><h3 className="detections">Keyword</h3></div>
          <div className="col-4"><h3 className="detections">Confidence</h3></div>
        </div>

        <div className="row text-center align-items-center mt-2">
          <div className="col-4"><h5 className="detected">{resultData.emotion}</h5></div>
          <div className="col-4"><h5 className="detected">“{resultData.keyword}”</h5></div>
          <div className="col-4">
            <h5
              className="detected"
              style={{
                color: resultData.confidence >= 80 ? "green" : resultData.confidence >= 60 ? "orange" : "red"
              }}
            >
              {resultData.confidence}%
            </h5>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="container" id="main-cont">
        <div className="row">
          {/* Left – Recommended Music */}
          <div className="col-md-4" id="music-sug">
            <h3 className="detections">
              <img
                src={`${process.env.PUBLIC_URL}/fyp-images/icon.png`}
                width="30"
                height="30"
                alt="Music Icon"
                className="logo-icon"
              />{" "}
              Recommended Music
            </h3>

            <div className="music-details">
              <h4 className="music-name">
                <img
                  src={`${process.env.PUBLIC_URL}/fyp-images/iconmusic.webp`}
                  width="35"
                  height="35"
                  alt="Music Icon"
                  className="logo-icon"
                />{" "}
                {resultData.songName}
              </h4>
              <p className="artist-name">{resultData.artist}</p>
              <p className="music-desc">{resultData.songDescription}</p>
            </div>
          </div>

          {/* Right – Generated Story */}
          <div className="col-md-7 offset-md-1" id="story">
            <h3 className="detections">
              <img
                src={`${process.env.PUBLIC_URL}/fyp-images/story.jpg`}
                width="30"
                height="30"
                alt="Story Icon"
                className="logo-icon2"
              />{" "}
              Generated Story
            </h3>

            <div className="story-details">
              <h5 className="detected-objects">Detected Objects:</h5>
              <div className="object-list">
                {resultData.detectedObjects.length > 0
                  ? resultData.detectedObjects.map((obj, index) => (
                      <span key={index} className="object-badge">{obj}</span>
                    ))
                  : <span className="object-badge">None</span>
                }
              </div>

              <p className="story-text">{resultData.story}</p>
            </div>
          </div>
        </div>
      </div>

      {/* Action Buttons */}
      <div className="action-buttons">
        <button className="tryanother-btn" onClick={() => navigate("/upload")}>
          🎥 Try Another Video
        </button>
        <button className="regen-btn" onClick={handleRegenerateStory}>
          🔄 Re-generate Story
        </button>
      </div>
    </div>
  );
}

export default Result;
